@font-face {
  font-family: Lato;
  src: url(https://tinybase.org/fonts/lato-light.woff2) format("woff2");
}
body {
  align-items: center;
  display: flex;
  font-family: Lato, sans-serif;
  height: 100vh;
  justify-content: center;
  margin: 0;
}

js:
const {createStore} = TinyBaseStore;
const {CellView, Provider} = TinyBaseUiReact;
const store = createStore();

const setTime = () => {
  store.setCell('t1', 'r1', 'c1', new Date().toLocaleTimeString());
};
setTime();

ReactDOM.render(
  <Provider store={store}>
    <CellView tableId="t1" rowId="r1" cellId="c1" />
  </Provider>,
  document.body,
);

setInterval(setTime, 1000);
