css:
@accentColor: #d81b60;
@spacing: 0.5rem;
@border: 1px solid #ccc;
@font-face {
  font-family: Inter;
  src: url(https://tinybase.org/fonts/inter.woff2) format("woff2");
}

body {
  display: grid;
  grid-template-columns: 35% minmax(0, 1fr);
  grid-template-rows: auto 1fr;
  box-sizing: border-box;
  font-family: Inter, sans-serif;
  letter-spacing: -0.04rem;
  grid-gap: @spacing * 2 @spacing;
  margin: 0;
  min-height: 100vh;
  padding: @spacing * 2;
  * {
    box-sizing: border-box;
    outline-color: @accentColor;
  }
}

#newTodo {
  border: @border;
  display: block;
  font: inherit;
  letter-spacing: inherit;
  padding: @spacing;
  width: 100%;
}

#todos {
  grid-column: 2;
  margin: 0;
  padding: 0;
}

#todos .todo {
  background: #fff;
  border: @border;
  display: flex;
  margin-bottom: @spacing;
  padding: @spacing;
  .text {
    cursor: pointer;
    flex: 1;
    overflow: hidden;
    text-overflow: ellipsis;
    user-select: none;
    white-space: nowrap;
    &::before {
      content: "\1F7E0";
      padding: 0 0.5rem 0 0.25rem;
    }
    &.done {
      color: #ccc;
      &::before {
        content: "\2705";
      }
    }
  }
}

js:
const {createStore} = TinyBase;
const {
  CellView,
  Provider,
  TableView,
  useAddRowCallback,
  useCell,
  useCreateStore,
  useSetCellCallback,
} = TinyBaseUiReact;
const {useCallback, useState} = React;

const INITIAL_TODOS = {
  todos: {
    0: {text: 'Clean the floor'},
    1: {text: 'Install TinyBase'},
    2: {text: 'Book holiday'},
  },
};

const App = () => {
  const store = useCreateStore(() => createStore().setTables(INITIAL_TODOS));

  return (
    <Provider store={store}>
      <Title />
      <NewTodo />
      <Todos />
    </Provider>
  );
};

window.addEventListener('load', () =>
  ReactDOM.createRoot(document.body).render(<App />),
);

const Title = () => 'Todos';

const NewTodo = () => {
  const [text, setText] = useState('');
  const handleChange = useCallback(({target: {value}}) => setText(value), []);
  const handleKeyDown = useAddRowCallback(
    'todos',
    ({which, target: {value: text}}) =>
      which == 13 && text != '' ? {text} : null,
    [],
    undefined,
    () => setText(''),
    [setText],
  );

  return (
    <input
      id="newTodo"
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      placeholder="New Todo"
      value={text}
    />
  );
};

const Todos = () => (
  <ul id="todos">
    <TableView tableId="todos" rowComponent={Todo} />
  </ul>
);

const Todo = (props) => (
  <li className="todo">
    <TodoText {...props} />
  </li>
);

const TodoText = ({tableId, rowId}) => {
  const done = useCell(tableId, rowId, 'done');
  const className = 'text' + (done ? ' done' : '');
  const handleClick = useSetCellCallback(tableId, rowId, 'done', () => !done, [
    done,
  ]);

  return (
    <span className={className} onClick={handleClick}>
      <CellView tableId={tableId} rowId={rowId} cellId="text" />
    </span>
  );
};
