css:
@accentColor: #d81b60;
@spacing: 0.5rem;
@border: 1px solid #ccc;
@font-face {
  font-family: Lato;
  src: url(https://tinybase.org/fonts/lato-light.woff2) format("woff2");
}
body {
  display: grid;
  grid-template-columns: 35% minmax(0, 1fr);
  grid-template-rows: auto 1fr;
  box-sizing: border-box;
  font-family: Lato, sans-serif;
  grid-gap: @spacing * 2 @spacing;
  margin: 0;
  min-height: 100vh;
  padding: @spacing * 2;
  * {
    box-sizing: border-box;
    outline-color: @accentColor;
  }
}

#newTodo {
  border: @border;
  display: block;
  font: inherit;
  padding: @spacing;
  width: 100%;
}

#todos {
  grid-column: 2;
  margin: 0;
  padding: 0;
}

#todos .todo {
  background: #fff;
  border: @border;
  display: flex;
  margin-bottom: @spacing;
  padding: @spacing;
  .text {
    cursor: pointer;
    flex: 1;
    overflow: hidden;
    text-overflow: ellipsis;
    user-select: none;
    white-space: nowrap;
    &::before {
      content: "\1F7E0";
      padding: 0 0.25rem;
    }
    &.done {
      color: #ccc;
      &::before {
        content: "\2705";
      }
    }
  }
}

#types {
  margin: 0;
}

#types .type {
  cursor: pointer;
  margin-bottom: @spacing;
  user-select: none;
  &.current {
    color: @accentColor;
  }
}

#todos .todo .type {
  border: none;
  color: #777;
  font: inherit;
  font-size: 0.8rem;
  margin-top: 0.1rem;
}

js:
const {
  createIndexes,
  createLocalPersister,
  createMetrics,
  createSessionPersister,
  createStore,
} = TinyBase;
const {
  CellView,
  Provider,
  SliceView,
  useAddRowCallback,
  useCell,
  useCreateIndexes,
  useCreateMetrics,
  useCreatePersister,
  useCreateStore,
  useMetric,
  useSetCellCallback,
} = TinyBaseUiReact;
const {useCallback, useState} = React;

const TYPES = ['Home', 'Work', 'Archived'];
const SCHEMA = {
  todos: {
    text: {type: 'string'},
    done: {type: 'boolean', default: false},
    type: {type: 'string', default: 'Home', allow: TYPES},
  },
};
const INITIAL_TODOS = {
  todos: {
    0: {text: 'Clean the floor', type: 'Home'},
    1: {text: 'Install TinyBase', type: 'Work'},
    2: {text: 'Book holiday', type: 'Archived'},
  },
};

const Demo = () => {
  const store = useCreateStore(() => createStore().setSchema(SCHEMA));
  useCreatePersister(
    store,
    (store) => createLocalPersister(store, 'todos/store'),
    [],
    async (persister) => {
      await persister.startAutoLoad(INITIAL_TODOS);
      await persister.startAutoSave();
    },
  );
  const sessionStore = useCreateStore(() => createStore());
  useCreatePersister(
    sessionStore,
    (store) => createSessionPersister(store, 'todos/sessionStore'),
    [],
    async (persister) => {
      await persister.startAutoLoad({ui: {current: {type: 'Home'}}});
      await persister.startAutoSave();
    },
  );
  const indexes = useCreateIndexes(store, (store) =>
    createIndexes(store).setIndexDefinition('types', 'todos', 'type'),
  );
  const metrics = useCreateMetrics(store, (store) => {
    const metrics = createMetrics(store);
    metrics.setMetricDefinition('pending', 'todos', 'sum', (getCell) =>
      !getCell('done') ? 1 : 0,
    );
    TYPES.forEach((type) => {
      metrics.setMetricDefinition(type, 'todos', 'sum', (getCell) =>
        getCell('type') == type && !getCell('done') ? 1 : 0,
      );
    });
    return metrics;
  });

  return (
    <Provider
      store={store}
      storesById={{sessionStore}}
      indexes={indexes}
      metrics={metrics}
    >
      <Title />
      <NewTodo />
      <Types />
      <Todos />
    </Provider>
  );
};

window.addEventListener('load', () => ReactDOM.render(<Demo />, document.body));

const Title = () => {
  const pending = useMetric('pending');

  return pending > 0 ? `Todo: ${pending}` : 'All done!';
};

const NewTodo = () => {
  const [text, setText] = useState('');
  const type = useCell('ui', 'current', 'type', 'sessionStore');
  const handleChange = useCallback(({target: {value}}) => setText(value), []);
  const handleKeyDown = useAddRowCallback(
    'todos',
    ({which, target: {value: text}}) =>
      which == 13 && text != '' ? {text, type} : null,
    [type],
    undefined,
    () => setText(''),
    [setText],
  );

  return (
    <input
      id="newTodo"
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      placeholder="New Todo"
      value={text}
    />
  );
};

const Todos = () => (
  <ul id="todos">
    <SliceView
      indexId="types"
      sliceId={useCell('ui', 'current', 'type', 'sessionStore')}
      rowComponent={Todo}
    />
  </ul>
);

const Todo = (props) => (
  <li className="todo">
    <TodoText {...props} />
    <TodoType {...props} />
  </li>
);

const TodoText = ({tableId, rowId}) => {
  const done = useCell(tableId, rowId, 'done');
  const className = 'text' + (done ? ' done' : '');
  const handleClick = useSetCellCallback(tableId, rowId, 'done', () => !done, [
    done,
  ]);

  return (
    <span className={className} onClick={handleClick}>
      <CellView tableId={tableId} rowId={rowId} cellId="text" />
    </span>
  );
};

const Types = () => (
  <ul id="types">
    {TYPES.map((type) => (
      <Type key={type} type={type} />
    ))}
  </ul>
);

const Type = ({type}) => {
  const pending = useMetric(type);
  const currentType = useCell('ui', 'current', 'type', 'sessionStore');
  const handleClick = useSetCellCallback(
    'ui',
    'current',
    'type',
    () => type,
    [type],
    'sessionStore',
  );
  const className = 'type' + (type == currentType ? ' current' : '');

  return (
    <li className={className} onClick={handleClick}>
      {type}
      {pending > 0 ? ` (${pending})` : ''}
    </li>
  );
};

const TodoType = ({tableId, rowId}) => {
  const type = useCell(tableId, rowId, 'type');
  const handleChange = useSetCellCallback(
    tableId,
    rowId,
    'type',
    ({target: {value}}) => value,
    [],
  );

  return (
    <select className="type" onChange={handleChange} value={type}>
      {TYPES.map((type) => (
        <option>{type}</option>
      ))}
    </select>
  );
};
