css:
@accentColor: #d81b60;
@spacing: 0.5rem;
@border: 1px solid #ccc;
@font-face {
  font-family: Inter;
  src: url(https://tinybase.org/fonts/inter.woff2) format("woff2");
}

body {
  display: grid;
  grid-template-columns: 35% minmax(0, 1fr);
  grid-template-rows: auto auto 1fr;
  box-sizing: border-box;
  font-family: Inter, sans-serif;
  letter-spacing: -0.04rem;
  grid-gap: @spacing * 2 @spacing;
  margin: 0;
  min-height: 100vh;
  padding: @spacing * 2;
  * {
    box-sizing: border-box;
    outline-color: @accentColor;
  }
}

#newTodo {
  border: @border;
  display: block;
  font: inherit;
  letter-spacing: inherit;
  padding: @spacing;
  width: 100%;
}

#todos {
  grid-column: 2;
  grid-row: 2 / span 2;
  margin: 0;
  padding: 0;
}

#todos .todo {
  background: #fff;
  border: @border;
  display: flex;
  margin-bottom: @spacing;
  padding: @spacing;
  .text {
    cursor: pointer;
    flex: 1;
    overflow: hidden;
    text-overflow: ellipsis;
    user-select: none;
    white-space: nowrap;
    &::before {
      content: "\1F7E0";
      padding: 0 0.5rem 0 0.25rem;
    }
    &.done {
      color: #ccc;
      &::before {
        content: "\2705";
      }
    }
  }
}

#types {
  margin: 0;
}

#types .type {
  cursor: pointer;
  margin-bottom: @spacing;
  user-select: none;
  &.current {
    color: @accentColor;
  }
}

#todos .todo .type {
  border: none;
  color: #777;
  font: inherit;
  font-size: 0.8rem;
  margin-top: 0.1rem;
}

#undoRedo {
  grid-column: 1;
  grid-row: 3;
  #undo,
  #redo {
    cursor: pointer;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    user-select: none;
    &::before {
      padding-right: 0.5rem;
      vertical-align: middle;
    }
    &.disabled {
      cursor: default;
      opacity: 0.3;
    }
  }
  #undo::before {
    content: "\21A9";
  }
  #redo::before {
    content: "\21AA";
  }
}

js:
const {
  createCheckpoints,
  createIndexes,
  createLocalPersister,
  createMetrics,
  createSessionPersister,
  createStore,
} = TinyBase;
const {
  CellView,
  CheckpointView,
  Provider,
  SliceView,
  useAddRowCallback,
  useCell,
  useCheckpoints,
  useCreateCheckpoints,
  useCreateIndexes,
  useCreateMetrics,
  useCreatePersister,
  useCreateStore,
  useMetric,
  useRedoInformation,
  useRow,
  useSetCellCallback,
  useSetCheckpointCallback,
  useSetValueCallback,
  useUndoInformation,
  useValue,
} = TinyBaseUiReact;
const {useCallback, useState} = React;

const TYPES = ['Home', 'Work', 'Archived'];
const SCHEMA = {
  todos: {
    text: {type: 'string'},
    done: {type: 'boolean', default: false},
    type: {type: 'string', default: 'Home', allow: TYPES},
  },
};
const INITIAL_TODOS = {
  todos: {
    0: {text: 'Clean the floor', type: 'Home'},
    1: {text: 'Install TinyBase', type: 'Work'},
    2: {text: 'Book holiday', type: 'Archived'},
  },
};

const App = () => {
  const store = useCreateStore(() => createStore().setTablesSchema(SCHEMA));
  const checkpoints = useCreateCheckpoints(store, createCheckpoints);
  useCreatePersister(
    store,
    (store) => createLocalPersister(store, 'todos/store'),
    [],
    async (persister) => {
      await persister.startAutoLoad(INITIAL_TODOS);
      checkpoints.clear();
      await persister.startAutoSave();
    },
  );
  const viewStore = useCreateStore(() => createStore());
  useCreatePersister(
    viewStore,
    (store) => createSessionPersister(store, 'todos/viewStore'),
    [],
    async (persister) => {
      await persister.startAutoLoad({}, {type: 'Home'});
      await persister.startAutoSave();
    },
  );
  const indexes = useCreateIndexes(store, (store) =>
    createIndexes(store).setIndexDefinition('types', 'todos', 'type'),
  );
  const metrics = useCreateMetrics(store, (store) => {
    const metrics = createMetrics(store);
    metrics.setMetricDefinition('pending', 'todos', 'sum', (getCell) =>
      !getCell('done') ? 1 : 0,
    );
    TYPES.forEach((type) => {
      metrics.setMetricDefinition(type, 'todos', 'sum', (getCell) =>
        getCell('type') == type && !getCell('done') ? 1 : 0,
      );
    });
    return metrics;
  });

  return (
    <Provider
      store={store}
      storesById={{viewStore}}
      indexes={indexes}
      metrics={metrics}
      checkpoints={checkpoints}
    >
      <Title />
      <NewTodo />
      <Types />
      <UndoRedo />
      <Todos />
    </Provider>
  );
};

window.addEventListener('load', () =>
  ReactDOM.createRoot(document.body).render(<App />),
);

const Title = () => {
  const pending = useMetric('pending');

  return pending > 0 ? `Todo: ${pending}` : 'All done!';
};

const NewTodo = () => {
  const [text, setText] = useState('');
  const type = useValue('type', 'viewStore');
  const handleChange = useCallback(({target: {value}}) => setText(value), []);
  const addCheckpoint = useSetCheckpointCallback(
    () => `adding '${text}'`,
    [text],
  );
  const handleKeyDown = useAddRowCallback(
    'todos',
    ({which, target: {value: text}}) =>
      which == 13 && text != '' ? {text, type} : null,
    [type],
    undefined,
    () => {
      setText('');
      addCheckpoint();
    },
    [setText, addCheckpoint],
  );

  return (
    <input
      id="newTodo"
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      placeholder="New Todo"
      value={text}
    />
  );
};

const Todos = () => (
  <ul id="todos">
    <SliceView
      indexId="types"
      sliceId={useValue('type', 'viewStore')}
      rowComponent={Todo}
    />
  </ul>
);

const Todo = (props) => (
  <li className="todo">
    <TodoText {...props} />
    <TodoType {...props} />
  </li>
);

const TodoText = ({tableId, rowId}) => {
  const {done, text} = useRow(tableId, rowId);
  const className = 'text' + (done ? ' done' : '');
  const setCell = useSetCellCallback(tableId, rowId, 'done', () => !done, [
    done,
  ]);
  const addCheckpoint = useSetCheckpointCallback(
    () => `${done ? 'resuming' : 'completing'} '${text}'`,
    [done],
  );
  const handleClick = useCallback(() => {
    setCell();
    addCheckpoint();
  }, [setCell, addCheckpoint]);

  return (
    <span className={className} onClick={handleClick}>
      <CellView tableId={tableId} rowId={rowId} cellId="text" />
    </span>
  );
};

const Types = () => (
  <ul id="types">
    {TYPES.map((type) => (
      <Type key={type} type={type} />
    ))}
  </ul>
);

const Type = ({type}) => {
  const pending = useMetric(type);
  const currentType = useValue('type', 'viewStore');
  const handleClick = useSetValueCallback(
    'type',
    () => type,
    [type],
    'viewStore',
  );
  const className = 'type' + (type == currentType ? ' current' : '');

  return (
    <li className={className} onClick={handleClick}>
      {type}
      {pending > 0 ? ` (${pending})` : ''}
    </li>
  );
};

const TodoType = ({tableId, rowId}) => {
  const type = useCell(tableId, rowId, 'type');
  const checkpoints = useCheckpoints();
  const handleChange = useSetCellCallback(
    tableId,
    rowId,
    'type',
    ({target: {value}}) => value,
    [],
    undefined,
    (_store, type) => checkpoints.addCheckpoint(`changing to '${type}'`),
    [checkpoints],
  );

  return (
    <select className="type" onChange={handleChange} value={type}>
      {TYPES.map((type) => (
        <option>{type}</option>
      ))}
    </select>
  );
};

const UndoRedo = () => {
  const [canUndo, handleUndo, , undoLabel] = useUndoInformation();
  const undo = canUndo ? (
    <div id="undo" onClick={handleUndo}>
      undo {undoLabel}
    </div>
  ) : (
    <div id="undo" className="disabled" />
  );

  const [canRedo, handleRedo, , redoLabel] = useRedoInformation();
  const redo = canRedo ? (
    <div id="redo" onClick={handleRedo}>
      redo {redoLabel}
    </div>
  ) : (
    <div id="redo" className="disabled" />
  );

  return (
    <div id="undoRedo">
      {undo}
      {redo}
    </div>
  );
};
