css:
#loading {
  animation: spin 1s infinite linear;
  height: 2rem;
  margin: 40vh auto;
  width: 2rem;
  &::before {
    content: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" height="2rem" viewBox="0 0 100 100"><path d="M50 10A40 40 0 1 1 10 50" stroke="black" fill="none" stroke-width="4" /></svg>');
  }
}

@keyframes spin {
  from {
    transform: rotate(0);
  }
  to {
    transform: rotate(360deg);
  }
}

@font-face {
  font-family: Inter;
  src: url(https://tinybase.org/fonts/inter.woff2) format("woff2");
}

* {
  box-sizing: border-box;
}

body {
  align-items: flex-start;
  color: #333;
  display: flex;
  font-family: Inter, sans-serif;
  font-size: 0.8rem;
  min-height: 100vh;
  justify-content: space-around;
  letter-spacing: -0.04rem;
  line-height: 1.5rem;
  margin: 0;
  user-select: none;
}

table {
  background: white;
  border-collapse: collapse;
  box-shadow: 0 0 1rem #0004;
  font-size: inherit;
  line-height: inherit;
  margin: 2rem;
  table-layout: fixed;
  th,
  td {
    overflow: hidden;
    padding: 0.25rem 0.5rem;
    white-space: nowrap;
    border-width: 1px 0;
    border-style: solid;
    border-color: #eee;
    text-align: left;
  }
  thead th {
    border-bottom-color: #ccc;
  }
  button,
  input {
    border: 1px solid #ccc;
  }
}

a {
  color: inherit;
}

js:
import {useMemo, useState} from 'react';
import React from 'react';
import {createRoot} from 'react-dom/client';
import {createRelationships, createStore} from 'tinybase';
import {
  CellView,
  Provider,
  useCell,
  useCreateRelationships,
  useCreateStore,
} from 'tinybase/ui-react';
import {RelationshipInHtmlTable} from 'tinybase/ui-react-dom';

const App = () => {
  const store = useCreateStore(createStore);
  const relationships = useCreateRelationships(store, (store) =>
    createRelationships(store).setRelationshipDefinition(
      'genresMetadata',
      'genres',
      'metadata',
      (_, rowId) => rowId + '_meta',
    ),
  );
  const [isLoading, setIsLoading] = useState(true);
  useMemo(async () => {
    await loadTable(store, 'genres');
    store.setTable('metadata', {
      g01_meta: {text: 'Dramatic movies to make you think', popularity: 6},
      g02_meta: {text: 'These ones make you laugh', popularity: 7},
      g03_meta: {text: 'Fun for all the family', popularity: 8},
      g04_meta: {text: 'For the romantics at heart', popularity: 5},
      g05_meta: {text: 'From cartoons to CGI', popularity: 5},
      g06_meta: {text: 'Escape to another world', popularity: 4},
      g07_meta: {text: 'Tales of the American West', popularity: 3},
      g08_meta: {text: 'Stay on the edge of your seat', popularity: 6},
      g09_meta: {text: 'For your inner explorer', popularity: 7},
      g10_meta: {text: 'Fast-paced action from start to finish', popularity: 8},
      g11_meta: {text: 'Jump scares to give you nightmares', popularity: 6},
      g12_meta: {text: 'Murders and mysteries', popularity: 5},
      g14_meta: {text: 'Take a step back in time', popularity: 3},
      g15_meta: {text: 'A glimpse of the future', popularity: 8},
      g16_meta: {text: 'Who did it?', popularity: 5},
    });
    setIsLoading(false);
  }, []);

  return (
    <Provider store={store} relationships={relationships}>
      {isLoading ? <Loading /> : <Body />}
    </Provider>
  );
};

addEventListener('load', () => createRoot(document.body).render(<App />));

const loadValues = (store) => {
  store
    .startTransaction()
    .setValue('username', 'John Appleseed')
    .setValue('email address', 'john.appleseed@example.com')
    .setValue('dark mode', true)
    .setValue('font size', 14)
    .finishTransaction();
};

const Loading = () => <div id="loading" />;

const Body = () => {
  return (
    <RelationshipInHtmlTable
      relationshipId="genresMetadata"
      customCells={customRelationshipCells}
      idColumn={false}
    />
  );
};

const NUMERIC = /^[\d\.]+$/;

const loadTable = async (store, tableId) => {
  store.startTransaction();
  const rows = (
    await (await fetch(`https://tinybase.org/assets/${tableId}.tsv`)).text()
  ).split('\n');
  const cellIds = rows.shift().split('\t');
  rows.forEach((row) => {
    const cells = row.split('\t');
    if (cells.length == cellIds.length) {
      const rowId = cells.shift();
      cells.forEach((cell, c) => {
        if (cell != '') {
          if (NUMERIC.test(cell)) {
            cell = parseFloat(cell);
          }
          store.setCell(tableId, rowId, cellIds[c + 1], cell);
        }
      });
    }
  });
  store.finishTransaction();
};

const DictionaryCell = ({tableId, rowId, cellId, store}) => {
  const word = useCell(tableId, rowId, cellId, store);
  return (
    <a
      href={'https://www.merriam-webster.com/dictionary/' + word}
      target="_blank"
    >
      {word}
    </a>
  );
};

const customCells = {name: {label: 'Name', component: DictionaryCell}};

const Popularity = (props) => (
  <b>
    <CellView {...props} />
  </b>
);

const customRelationshipCells = {
  'genres.name': 'Genre',
  'metadata.popularity': {
    label: 'Popularity',
    component: Popularity,
  },
  'metadata.text': 'Description',
};
